                'mineral'=>$mItem['material_mineral']
            ];
            if ($mItem['material_type'] == 1 && $mItem['material_parent'] != 0 && $mItem['material_condition'] > 0) {
                $metalData[] = [
                    'material_id'=>$mItem['material_id'],
                    'condition'=>$mItem['material_condition'],
                    'chemistry'=>$mItem['material_chemistry'],
                    'chinese'=>$mItem['material_chinese'],
                    'english'=>$mItem['material_english'],
                    'metal'=>$mItem['material_type_sub']
                ];
            }
        }

        foreach ($vList as $vItem) {
            $vData[] = [
                'id'=>$vItem['variety_id'],
                'code'=>$vItem['variety_code'],
                'parent'=>$vItem['variety_parent'],
                'name'=>$vItem['variety_name'],
                'pinyin'=>pinyin($vItem['variety_name']),
                'letter'=>letter($vItem['variety_name']),
                'description'=>$vItem['variety_description'],
                'type'=>$vItem['variety_type']
            ];
        }

        if (!DB::table('materials')->count()) {
            DB::table('materials')->insert($mData);
            DB::table('materials_metals')->insert($metalData);
        }

        if (!DB::table('varieties')->count()) {
            DB::table('varieties')->insert($vData);
        }
	}

}
